tailwind.withVariables:
@import "tailwindcss";
@config "../../tailwind.config.js";

@layer base {
  /* Override Tailwind 4 default hover cursor for buttons and button-like elements */
  button:not(:disabled),
  [role="button"]:not(:disabled) {
    cursor: pointer;
  }

  :root {
    --radius: 0.5rem;
  }

  :root {
    --background: 0 0% 100%;
    --foreground: 20.9 91.7% 14.1%;
    --muted: 48 96.5% 88.8%;
    --muted-foreground: 37.7 92.1% 50.2%;
    --popover: 0 0% 100%;
    --popover-foreground: 20.9 91.7% 14.1%;
    --card: 0 0% 100%;
    --card-foreground: 20.9 91.7% 14.1%;
    --border: 48 96.6% 76.7%;
    --input: 48 96.6% 76.7%;
    --primary: 21.7 77.8% 26.5%;
    --primary-foreground: 48 100% 96.1%;
    --secondary: 48 96.5% 88.8%;
    --secondary-foreground: 21.7 77.8% 26.5%;
    --accent: 48 96.5% 88.8%;
    --accent-foreground: 21.7 77.8% 26.5%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 48 100% 96.1%;
    --ring: 20.9 91.7% 14.1%;
  }

  .dark {
    --background: 20.9 91.7% 14.1%;
    --foreground: 48 100% 96.1%;
    --muted: 22.7 82.5% 31.4%;
    --muted-foreground: 43.3 96.4% 56.3%;
    --popover: 20.9 91.7% 14.1%;
    --popover-foreground: 48 100% 96.1%;
    --card: 20.9 91.7% 14.1%;
    --card-foreground: 48 100% 96.1%;
    --border: 22.7 82.5% 31.4%;
    --input: 22.7 82.5% 31.4%;
    --primary: 48 100% 96.1%;
    --primary-foreground: 21.7 77.8% 26.5%;
    --secondary: 22.7 82.5% 31.4%;
    --secondary-foreground: 48 100% 96.1%;
    --accent: 22.7 82.5% 31.4%;
    --accent-foreground: 48 100% 96.1%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 48 100% 96.1%;
    --ring: 45.9 96.7% 64.5%;
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }

  .fade-slide-top-enter-active,
  .fade-slide-top-leave-active {
    transition: all 0.3s ease;
  }

  .fade-slide-top-enter-from,
  .fade-slide-top-leave-to {
    opacity: 0;
    filter: blur(4px);
    transform: translateY(8px);
  }
}

css.withVariables:
@layer base {
  /* Override preflight default hover cursor for buttons and button-like elements */
  button:not(:disabled),
  [role="button"]:not(:disabled) {
    cursor: pointer;
  }

  :root {
    --radius: 0.5rem;
    --radius-full: 9999px;
    --radius-xl: calc(var(--radius) + 4px);
    --radius-lg: var(--radius);
    --radius-md: calc(var(--radius) - 2px);
    --radius-sm: calc(var(--radius) - 4px);
    --radius-xs: min(calc(var(--radius) / 2.5), 6px);
    --font-mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
    --shadow-md: 
      0 4px 6px -1px rgba(0, 0, 0, 0.02), 
      0 2px 4px -1px rgba(0, 0, 0, 0.02),
      0 12px 24px rgba(0, 0, 0, 0.05);
  }

  :root {
    --background: 0 0% 100%;
    --foreground: 20.9 91.7% 14.1%;
    --muted: 48 96.5% 88.8%;
    --muted-foreground: 37.7 92.1% 50.2%;
    --popover: 0 0% 100%;
    --popover-foreground: 20.9 91.7% 14.1%;
    --card: 0 0% 100%;
    --card-foreground: 20.9 91.7% 14.1%;
    --border: 48 96.6% 76.7%;
    --input: 48 96.6% 76.7%;
    --primary: 21.7 77.8% 26.5%;
    --primary-foreground: 48 100% 96.1%;
    --secondary: 48 96.5% 88.8%;
    --secondary-foreground: 21.7 77.8% 26.5%;
    --accent: 48 96.5% 88.8%;
    --accent-foreground: 21.7 77.8% 26.5%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 48 100% 96.1%;
    --ring: 20.9 91.7% 14.1%;
  }

  .dark {
    --background: 20.9 91.7% 14.1%;
    --foreground: 48 100% 96.1%;
    --muted: 22.7 82.5% 31.4%;
    --muted-foreground: 43.3 96.4% 56.3%;
    --popover: 20.9 91.7% 14.1%;
    --popover-foreground: 48 100% 96.1%;
    --card: 20.9 91.7% 14.1%;
    --card-foreground: 48 100% 96.1%;
    --border: 22.7 82.5% 31.4%;
    --input: 22.7 82.5% 31.4%;
    --primary: 48 100% 96.1%;
    --primary-foreground: 21.7 77.8% 26.5%;
    --secondary: 22.7 82.5% 31.4%;
    --secondary-foreground: 48 100% 96.1%;
    --accent: 22.7 82.5% 31.4%;
    --accent-foreground: 48 100% 96.1%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 48 100% 96.1%;
    --ring: 45.9 96.7% 64.5%;
  }

  * {
    border-color: hsl(var(--border));
  }

  body {
    background-color: hsl(var(--background));
    color: hsl(var(--foreground));
  }

  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border-width: 0;
  }

  @keyframes fade-in {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  @keyframes spin {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  @keyframes collapsible-down {
    from {
      height: 0;
    }
    to {
      height: var(--reka-collapsible-content-height);
    }
  }

  @keyframes collapsible-up {
    from {
      height: var(--reka-collapsible-content-height);
    }
    to {
      height: 0;
    }
  }

  .animate-fade-in {
    animation: fade-in 0.5s ease;
  }

  .animate-spin {
    animation: spin 1s linear infinite;
  }

  .fade-slide-top-enter-active,
  .fade-slide-top-leave-active {
    transition: all 0.3s ease;
  }

  .fade-slide-top-enter-from,
  .fade-slide-top-leave-to {
    opacity: 0;
    filter: blur(4px);
    transform: translateY(8px);
  }
}
