tailwind.withVariables:
@import "tailwindcss";
@config "../../tailwind.config.js";

@layer base {
  /* Override Tailwind 4 default hover cursor for buttons and button-like elements */
  button:not(:disabled),
  [role="button"]:not(:disabled) {
    cursor: pointer;
  }

  :root {
    --backdrop-filter-blur: 32px;
    --radius: 0.5rem;

    --background: 0 0% 100%;
    --foreground: 224 71.4% 4.1%;
    --muted: 220 14.3% 95.9%;
    --muted-foreground: 220 8.9% 46.1%;
    --popover: 0 0% 100% / 10%;
    --popover-foreground: 224 71.4% 4.1%;
    --card: 0 0% 100% / 10%;
    --card-foreground: 224 71.4% 4.1%;
    --border: 220 13% 91%;
    --input: 0 0% 100%;
    --primary: 220.9 39.3% 11%;
    --primary-foreground: 210 20% 98%;
    --secondary: 220 14.3% 95.9%;
    --secondary-foreground: 220.9 39.3% 11%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 210 20% 98%;
    --ring: 224 71.4% 4.1%;
  }

  .dark {
    --background: 224 71.4% 4.1%;
    --foreground: 210 20% 98%;
    --muted: 215 27.9% 16.9%;
    --muted-foreground: 217.9 10.6% 64.9%;
    --popover: 224 71.4% 4.1% / 20%;
    --popover-foreground: 210 20% 98%;
    --card: 215 27.9% 16.9% / 30%;
    --card-foreground: 210 20% 98%;
    --border: 215 27.9% 16.9%;
    --input: 224 71.4% 4.1%  / 50%;
    --primary: 210 20% 98%;
    --primary-foreground: 220.9 39.3% 11%;
    --secondary: 215 27.9% 16.9% / 50%;
    --secondary-foreground: 210 20% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 20% 98%;
    --ring: 216 12.2% 83.9%;
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }

  .fade-slide-top-enter-active,
  .fade-slide-top-leave-active {
    transition: all 0.3s ease;
  }

  .fade-slide-top-enter-from,
  .fade-slide-top-leave-to {
    opacity: 0;
    filter: blur(4px);
    transform: translateY(8px);
  }
}

css.withVariables:
@layer base {
  /* Override preflight default hover cursor for buttons and button-like elements */
  button:not(:disabled),
  [role="button"]:not(:disabled) {
    cursor: pointer;
  }

  :root {
    --backdrop-filter-blur: 32px;
    --radius: 0.5rem;
    --radius-full: 9999px;
    --radius-xl: calc(var(--radius) + 4px);
    --radius-lg: var(--radius);
    --radius-md: calc(var(--radius) - 2px);
    --radius-sm: calc(var(--radius) - 4px);
    --radius-xs: min(calc(var(--radius) / 2.5), 6px);
    --font-mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
    --shadow-md: 
      0 4px 6px -1px rgba(0, 0, 0, 0.02), 
      0 2px 4px -1px rgba(0, 0, 0, 0.02),
      0 12px 24px rgba(0, 0, 0, 0.05);

    --background: 0 0% 100%;
    --foreground: 224 71.4% 4.1%;
    --muted: 220 14.3% 95.9%;
    --muted-foreground: 220 8.9% 46.1%;
    --popover: 0 0% 100% / 10%;
    --popover-foreground: 224 71.4% 4.1%;
    --card: 0 0% 100% / 10%;
    --card-foreground: 224 71.4% 4.1%;
    --border: 220 13% 91%;
    --input: 0 0% 100%;
    --primary: 220.9 39.3% 11%;
    --primary-foreground: 210 20% 98%;
    --secondary: 220 14.3% 95.9%;
    --secondary-foreground: 220.9 39.3% 11%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 210 20% 98%;
    --ring: 224 71.4% 4.1%;
  }

  .dark {
    --background: 224 71.4% 4.1%;
    --foreground: 210 20% 98%;
    --muted: 215 27.9% 16.9%;
    --muted-foreground: 217.9 10.6% 64.9%;
    --popover: 224 71.4% 4.1% / 20%;
    --popover-foreground: 210 20% 98%;
    --card: 215 27.9% 16.9% / 30%;
    --card-foreground: 210 20% 98%;
    --border: 215 27.9% 16.9%;
    --input: 224 71.4% 4.1%  / 50%;
    --primary: 210 20% 98%;
    --primary-foreground: 220.9 39.3% 11%;
    --secondary: 215 27.9% 16.9% / 50%;
    --secondary-foreground: 210 20% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 20% 98%;
    --ring: 216 12.2% 83.9%;
  }

  * {
    border-color: hsl(var(--border));
  }

  body {
    background-color: hsl(var(--background));
    color: hsl(var(--foreground));
  }

  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border-width: 0;
  }

  @keyframes fade-in {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  @keyframes spin {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  @keyframes collapsible-down {
    from {
      height: 0;
    }
    to {
      height: var(--reka-collapsible-content-height);
    }
  }

  @keyframes collapsible-up {
    from {
      height: var(--reka-collapsible-content-height);
    }
    to {
      height: 0;
    }
  }

  .animate-fade-in {
    animation: fade-in 0.5s ease;
  }

  .animate-spin {
    animation: spin 1s linear infinite;
  }

  .fade-slide-top-enter-active,
  .fade-slide-top-leave-active {
    transition: all 0.3s ease;
  }

  .fade-slide-top-enter-from,
  .fade-slide-top-leave-to {
    opacity: 0;
    filter: blur(4px);
    transform: translateY(8px);
  }
}
