tailwind.withVariables:
@import "tailwindcss";
@config "../../tailwind.config.js";

@layer base {
  /* Override Tailwind 4 default hover cursor for buttons and button-like elements */
  button:not(:disabled),
  [role="button"]:not(:disabled) {
    cursor: pointer;
  }

  :root {
    --radius: 0.5rem;
  }

  :root {
    --background: 0 0% 100%;
    --foreground: 0 74.7% 15.5%;
    --muted: 0 93.3% 94.1%;
    --muted-foreground: 0 84.2% 60.2%;
    --popover: 0 0% 100%;
    --popover-foreground: 0 74.7% 15.5%;
    --card: 0 0% 100%;
    --card-foreground: 0 74.7% 15.5%;
    --border: 0 96.3% 89.4%;
    --input: 0 96.3% 89.4%;
    --primary: 0 62.8% 30.6%;
    --primary-foreground: 0 85.7% 97.3%;
    --secondary: 0 93.3% 94.1%;
    --secondary-foreground: 0 62.8% 30.6%;
    --accent: 0 93.3% 94.1%;
    --accent-foreground: 0 62.8% 30.6%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 85.7% 97.3%;
    --ring: 0 74.7% 15.5%;
  }

  .dark {
    --background: 0 74.7% 15.5%;
    --foreground: 0 85.7% 97.3%;
    --muted: 0 70% 35.3%;
    --muted-foreground: 0 90.6% 70.8%;
    --popover: 0 74.7% 15.5%;
    --popover-foreground: 0 85.7% 97.3%;
    --card: 0 74.7% 15.5%;
    --card-foreground: 0 85.7% 97.3%;
    --border: 0 70% 35.3%;
    --input: 0 70% 35.3%;
    --primary: 0 85.7% 97.3%;
    --primary-foreground: 0 62.8% 30.6%;
    --secondary: 0 70% 35.3%;
    --secondary-foreground: 0 85.7% 97.3%;
    --accent: 0 70% 35.3%;
    --accent-foreground: 0 85.7% 97.3%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 0 85.7% 97.3%;
    --ring: 0 93.5% 81.8%;
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }

  .fade-slide-top-enter-active,
  .fade-slide-top-leave-active {
    transition: all 0.3s ease;
  }

  .fade-slide-top-enter-from,
  .fade-slide-top-leave-to {
    opacity: 0;
    filter: blur(4px);
    transform: translateY(8px);
  }
}

css.withVariables:
@layer base {
  /* Override preflight default hover cursor for buttons and button-like elements */
  button:not(:disabled),
  [role="button"]:not(:disabled) {
    cursor: pointer;
  }

  :root {
    --radius: 0.5rem;
    --radius-full: 9999px;
    --radius-xl: calc(var(--radius) + 4px);
    --radius-lg: var(--radius);
    --radius-md: calc(var(--radius) - 2px);
    --radius-sm: calc(var(--radius) - 4px);
    --radius-xs: min(calc(var(--radius) / 2.5), 6px);
    --font-mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
    --shadow-md: 
      0 4px 6px -1px rgba(0, 0, 0, 0.02), 
      0 2px 4px -1px rgba(0, 0, 0, 0.02),
      0 12px 24px rgba(0, 0, 0, 0.05);
  }

  :root {
    --background: 0 0% 100%;
    --foreground: 0 74.7% 15.5%;
    --muted: 0 93.3% 94.1%;
    --muted-foreground: 0 84.2% 60.2%;
    --popover: 0 0% 100%;
    --popover-foreground: 0 74.7% 15.5%;
    --card: 0 0% 100%;
    --card-foreground: 0 74.7% 15.5%;
    --border: 0 96.3% 89.4%;
    --input: 0 96.3% 89.4%;
    --primary: 0 62.8% 30.6%;
    --primary-foreground: 0 85.7% 97.3%;
    --secondary: 0 93.3% 94.1%;
    --secondary-foreground: 0 62.8% 30.6%;
    --accent: 0 93.3% 94.1%;
    --accent-foreground: 0 62.8% 30.6%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 85.7% 97.3%;
    --ring: 0 74.7% 15.5%;
  }

  .dark {
    --background: 0 74.7% 15.5%;
    --foreground: 0 85.7% 97.3%;
    --muted: 0 70% 35.3%;
    --muted-foreground: 0 90.6% 70.8%;
    --popover: 0 74.7% 15.5%;
    --popover-foreground: 0 85.7% 97.3%;
    --card: 0 74.7% 15.5%;
    --card-foreground: 0 85.7% 97.3%;
    --border: 0 70% 35.3%;
    --input: 0 70% 35.3%;
    --primary: 0 85.7% 97.3%;
    --primary-foreground: 0 62.8% 30.6%;
    --secondary: 0 70% 35.3%;
    --secondary-foreground: 0 85.7% 97.3%;
    --accent: 0 70% 35.3%;
    --accent-foreground: 0 85.7% 97.3%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 0 85.7% 97.3%;
    --ring: 0 93.5% 81.8%;
  }

  * {
    border-color: hsl(var(--border));
  }

  body {
    background-color: hsl(var(--background));
    color: hsl(var(--foreground));
  }

  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border-width: 0;
  }

  @keyframes fade-in {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  @keyframes spin {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  @keyframes collapsible-down {
    from {
      height: 0;
    }
    to {
      height: var(--reka-collapsible-content-height);
    }
  }

  @keyframes collapsible-up {
    from {
      height: var(--reka-collapsible-content-height);
    }
    to {
      height: 0;
    }
  }

  .animate-fade-in {
    animation: fade-in 0.5s ease;
  }

  .animate-spin {
    animation: spin 1s linear infinite;
  }

  .fade-slide-top-enter-active,
  .fade-slide-top-leave-active {
    transition: all 0.3s ease;
  }

  .fade-slide-top-enter-from,
  .fade-slide-top-leave-to {
    opacity: 0;
    filter: blur(4px);
    transform: translateY(8px);
  }
}
